CREATE TABLE TA_CONTROLE
(
  ID_CONTROLE DID3 NOT NULL,
  CHAMP DLIB255NN,
  CONTEXTE DLIB100,
  CLIENT DLIB100,
  SERVEUR DLIB100,
  CONTROLE_DEFAUT DLIB255NN,
  CONTROLE_UTILISATEUR DLIB255,
  QUI_CREE DLIB50,
  QUAND_CREE TIMESTAMP DEFAULT 'NOW',
  QUI_MODIF DLIB50,
  QUAND_MODIF TIMESTAMP DEFAULT 'NOW',
  "VERSION" NUM_VERSION,
  IP_ACCES DLIB50NN DEFAULT 0,
  VERSION_OBJ INTEGER
);

set term ^;
CREATE OR ALTER TRIGGER TBI_TA_CONTROLE FOR TA_CONTROLE
ACTIVE BEFORE INSERT POSITION 0
as
begin
   If (New.ID_TA_CONTROLE is null) Then
      New.ID_TA_CONTROLE = GEN_ID(NUM_ID_TA_CONTROLE,1);
   New.QUI_CREE = USER;
   new.QUAND_CREE = 'NOW';
   new.QUI_MODIF = USER;
   new.QUAND_MODIF = 'NOW';
   new.IP_ACCES = current_connection;
   select num_version from ta_version into new."VERSION";
end
^

CREATE OR ALTER TRIGGER TBU_TA_CONTROLE FOR TA_CONTROLE
ACTIVE BEFORE UPDATE POSITION 1
as
begin
   new.QUI_MODIF = USER;
   new.QUAND_MODIF = 'NOW';
   new.IP_ACCES = current_connection;
   select num_version from ta_version into new."VERSION";
end
^



